import type { Metadata } from "next";
import { Geist, Geist_Mono } from "next/font/google";
import "./globals.css";
import Link from "next/link";
import Script from "next/script";

const geistSans = Geist({
  variable: "--font-geist-sans",
  subsets: ["latin"],
});

const geistMono = Geist_Mono({
  variable: "--font-geist-mono",
  subsets: ["latin"],
});

export const metadata: Metadata = {
  title: "Next App with Sidebar",
  description: "Next.js Bootstrap Sidebar Layout",
};

export default function RootLayout({
  children,
}: {
  children: React.ReactNode;
}) {
  return (
    <html lang="en">
      <head>
        {/* Bootstrap CSS */}
        <link
          href="https://cdn.jsdelivr.net/npm/bootstrap@5.0.2/dist/css/bootstrap.min.css"
          rel="stylesheet"
        />
      </head>

      <body className={`${geistSans.variable} ${geistMono.variable} bg-light`}>

        {/* NavBar */}
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark px-3">
          <button
            className="btn btn-outline-light me-3"
            data-bs-toggle="offcanvas"
            data-bs-target="#sidebar"
          >
            â˜°
          </button>

          <Link className="navbar-brand" href="/">
            MyApp
          </Link>

          <div className="collapse navbar-collapse">
            <ul className="navbar-nav ms-auto">
              <li className="nav-item">
                <Link className="nav-link" href="/">Home</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" href="/about">About</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" href="/contact">Contact</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" href="/services">Services</Link>
              </li>
            </ul>
          </div>
        </nav>

       {/* SideBar */}
        <div
          className="offcanvas offcanvas-start"
          tabIndex={-1}
          id="sidebar"
        >
          <div className="offcanvas-header">
            <h5 className="offcanvas-title">Dashboard</h5>
            <button
              type="button"
              className="btn-close"
              data-bs-dismiss="offcanvas"
            ></button>
          </div>

          <div className="offcanvas-body">
            <ul className="list-group list-group-flush">
              <Link href="/" className="list-group-item list-group-item-action">
                Home
              </Link>
              <Link href="/services" className="list-group-item list-group-item-action">
               Services
              </Link>
              <Link href="/about" className="list-group-item list-group-item-action">
                 About
              </Link>
              <Link href="/contact" className="list-group-item list-group-item-action">
               Contact
              </Link>
            </ul>
          </div>
        </div>

        {/* Pages */}
        <main className="container mt-4">
          {children}
        </main>

        {/* Bootstrap Script */}
        <Script
          src="https://cdn.jsdelivr.net/npm/bootstrap@5.0.2/dist/js/bootstrap.bundle.min.js"
          strategy="afterInteractive"
        />
      </body>
    </html>
  );
}

